css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp(".*.pretendyoure.xyz/zy/game.jsp.*") {
body {
    background-color: #181F23 !important;
    color: white !important;
}
.scorecard:nth-child(odd) {
    background-color: #273138 !important;
}
.scorecard:nth-child(even) {
    background-color: #20282D !important;
}
.game_options {
    background-color: #2C363D !important;
    border-radius: 5px;
}
#tabs {
    background-image: none !important;
    background-color: #333 !important;
}
.ui-tabs-nav {
    background-image: none !important;
    background-color: #303940 !important;
    border: none !important;
}
.ui-state-default {
    background: none !important;
    background-color: #222 !important;
    border: 3px white !important;
}
.ui-state-active {
    background-color: #465159 !important;
}
.log {
    background-color: #273138 !important;
}
.log span {
    color: #eee !important;
}
.log span:nth-of-type(even) {
    background-color: #222;
}
.chat {
    background-color: #273138 !important;
    color: white !important;
}
.ui-tabs-panel {
    background-color: #303940 !important;
    color: white !important;
}
.ui-tabs-panel select, .ui-tabs-panel textarea {
    background-color: #303940 !important;
    color: white !important;
}
.game_hand_filter_text {
    background-color: #333 !important;
}
.whitecard {
    background: none;
    background-color: #313D46 !important;
    color: white;
}
.whitecard.selected {
    background-color: #273138 !important;
}
.gamelist_lobby {
    background: none;
    background-color: #222 !important;
}
.game_hand_cards {
    padding-bottom: 10px;
    padding-top: 10px;
}
}

@-moz-document domain("pretendyoure.xyz") {
body {
    background: #181F23 !important;
    color: white !important;
}
a {
    color: #337ab7 !important;
}
a:visited {
    color: #CCFFFF !important;
}
#menubar {
    background: #313D46 !important;
    color: white !important;
}
}
