css:
@-moz-document domain("www.unknowncheats.me") {
table,
div,
tbody {
    background-color: #191e1d;
    color: #c8d6e5;
}
.alt1,
.alt2,
.info,
.tcat,
.vbmenu_control,
.thead,
.alt1Active,
#toc,
#ca-view > span > a,
#ca-nstab-main > span > a,
#ca-talk > span > a,
.mw-body,
h1,
h2,
h3,
h4,
h5,
h6 {
    background-color: #191e1d;
    background-image: none;
    border-style: hidden;
    color: #c8d6e5;
}
a:visited {
    color: #096b52;
}
strong {
    color: #1dd1a1;
}
.navbar {
    color: #576574;
}
.navbar > a {
    color: #1dd1a1;
}
font,
a,
a:link,
.thead a:link,
span {
    color: #1dd1a1;
}
.time {
    color: #c8d6e5;
}
hr {
    border: hidden;
    border-color: #576574;
}
.smallfont {
    color: #c8d6e5;
}
.vbmenu_option a:link,
.vbmenu_option_alink {
    background-color: #191e1d;
    color: #c8d6e5;
}
.thead a:visited,
.thead_avisited {
    color: #c8d6e5;
}
.inlineimg {
    filter: contrast(50%) saturate(120%);
}
.tfoot {
    background: none;
    color: #1dd1a1;
}
legend {
    color: #1dd1a1;
}
.tfoot a:link,
.tfoot_alink {
    color: #1dd1a1;
}
textarea,
input {
    background-color: #576574;
    border: hidden;
    border-color: #c8d6e5;
    color: #c8d6e5;
}
.panelsurround,
.panel,
.vBulletin_editor,
.imagebutton {
    background-color: #191e1d;
    background: #191e1d;
    border: hidden;
}
select {
    background-color: #c8d6e5;
    border: hidden;
    color: #191e1d;
}
.prettyprinted {
    background-color: #2d3436;
    border: hidden;
    color: #c8d6e5;
}
ol.linenums {
    background-color: #2d3436;
}
.linenums > li {
    background-color: #2d3436;
    color: white;
}
.linenums > li > span {
    filter: invert(100%);
}
.alt2 {
    border: hidden!important;
}
.tcat a:visited,
li[id^="footer-"],
a,
div.portal h3 {
    color: #1dd1a1!important;
}
table.wikitable > * > tr > th {
    background-color: #2d3436!important;
    color: #c8d6e5;
    border: hidden;
}
.mediawiki {
    background-color: #191e1d;
}
caption {
    color: #c8d6e5;
}
#bodyContent > #mw-content-text > p > span > span {
    color: #fb5b5b!important;
}
}
