css:
body{
    background-image:url("https://thumbs.dreamstime.com/b/seamless-water-texture-computer-graphic-big-collection-abstract-textures-35628206.jpg")
}

.container{
    font-family:Courier New;
}

h2{
    font-weight:bold;
    font-size:48;
    border-bottom:3px solid #fff;
    border-radius:3px;
    border-top:3px solid #fff;
    color:#fff;
}

.form-group{
    text-align:center;
}

.control-label{
    color:#fff;
    font-size:20;
}

.form-control{
    border-radius:20px;
    height:20;
    border:none;
}

hr{
    border:none;
}

.alert{
    border-radius:30px;
    height:50;
    border:none;
    background-color:#fff;
    color:#00d0ff;
    font-size:18;
    font-weight:bold;
}

.btn{
    border-radius:20px;
    border:none;
    background-color:#00d0ff;
    color:#fff;
    transition-duration:0.4s;
}

.btn:hover{
    background-color:#fff;
    color:#00d0ff;
}

a{
    color:#fff;
    font-weight:bold;
    transition-duration:0.2s;
}

a:hover{
    text-decoration:none;
    color:#000;
}

