@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {
#deviant-commands .i4,
#deviant-commands .i5,
#deviant-commands .i16,
#deviant-commands .i17,
#deviant-commands .i7,
#deviant-commands .i8,
#deviant-commands .i6,
#deviant-commands .i9 {background-image:none!important;}

#deviant-commands a[href*="submit"],
#deviant-commands a[href*="my.deviantart.com/journal/"],
#deviant-commands a[href*="my.deviantart.com/account/"],
#deviant-commands a[href*="my.deviantart.com/wishlist/"],
#deviant-commands a[href*="my.deviantart.com/profile/"],
#deviant-commands a[href*="my.deviantart.com/settings/"],
#deviant-commands a[href*="my.deviantart.com/services/"],
#deviant-commands a[href*="store"] {display:none;}
}
