css:
@-moz-document domain("swin.edu.au") {
/*** Only optimized for iLearn. And even then, it's not exactly brilliant.  ***/
.topTabs,
body {
    background: #090909!important;
    border: #090909!important;
    color: #fff!important;
}
.paneTabs,
.portlet h2,
.stream_page_left {
    background: #222!important;
    color: #e8e8e8!important;
}
a {
    color: #FF0000!important;
}
.active a,
.active_stream_item,
.stream_datestamp,
.stream_item,
.stream_area_name,
.grade-value,
element.style,
h3,
span {
    color: #fff!important
}
.contentBox,
.container {
    background: #111!important;
    background-color: #111!important;
    color: #e8e8e8!important;
}
.collapsible,
.accordion_content,
.flyout-menu,
.accordion_toggle span,
.breadcrumbs,
.contentPane,
.shadow,
.cssTtableFilterBarMain,
.cssTtableNavHdrReqStts,
.cssTtableNavHdrReq,
.cssTtableNavHdrComp,
.cssTtableNavHdrComp,
.cssTtableFlexiExtPanelBody,
.cssTtableFlexiExtPanelTab,
.cssTtableFlexiExtPanelLegend,
.cssTtbleColTimeline {
    background-color: #333!important;
    background: #333!important;
    border-top: #444!important;
    border-left: #444!important;
    border-right: #444!important;
    border-bottom: #444!important;
    color: #fff!important;
}
#mc-search-box {
    background-color: rgba(0, 0, 0, 0)!important;
}
input {
    border: rgba(0, 0, 0, 0);
    background-color: #333;
    color: #fff;
}
.cssTtbleColDay,
.cssTtbleColBody {
    background-color: #333!important;
}
}
