css:
@-moz-document domain("prototypr.io"), domain("blog.framer.com"), domain("uxplanet.org"), domain("medium.freecodecamp.org"), domain("medium.muz.li"), domain("upscri.be"), domain("hackernoon.com"), domain("writingcooperative.com"), domain("medium.com"), domain("500ish.com"), domain("uxdesign.cc"), domain("blog.productdisrupt.com"), domain("journal.thriveglobal.com"), domain("blog.figma.com"), domain("designpub.ru"), domain("yvr.design"), domain("blog.markgrowth.com"), domain("eand.co") {
/*
    COLOR
*/

:root {
    --color_bg0: #0e0e0f;
    --color_bg1: #131314;
    --color_bg2: #1a1b1c;
    --color_bg3: #212224;
    --color_bg4: #2f3133;
    --color_bg5: #37393b;
    --color_bg6: #3e4042;
    --color_bg7: #46484a;
    --color_bg8: #4e5052;
    
    --color_bg1_o09: rgba(29, 29, 29, 0.9);
    --color_accent1: #324038;
    --color_accent2: #00ab6b;
    --color_accent3: #02b875;
    --color_accent4: #02ed97;
    
    --color_border1: rgba(255, 255, 255, 0.03);
    --color_border2: rgba(255, 255, 255, 0.06);
    
    --color_fg1: #a6a6a6;
    --color_fg2: #b3b3b3;
    --color_fg3: #bfbfbf;
    --color_fg4: #cccccc;
    --color_fg5: #d9d9d9;
    --color_fg6: #e5e5e5;
    --color_fg7: #f2f2f2;
}

/*
    BACKGROUNDS
*/
.k{
    background-color:var(--color_bg2);
}

/* 
    FOREGROUNDS
*/
.cp{
    color:var(--color_fg2);
}
.cc{
    color:var(--color_fg3);
}
h1[class^="elevate"]{
    color:var(--color_fg1);
}
p[class^="elevate"]
{
    color:var(--color_fg1);
}
}
