css:
@-moz-document url-prefix("https://speech-to-text-demo.ng.bluemix.net/") {
/*deleting elements */

.header,.jumbotron,.gdpr-info,.base--h2,.base--li,.smalltext,.column>p:nth-of-type(1),.base--select,.dropzone>div:nth-of-type(3),.base--button:nth-of-type(2),.base--button:nth-of-type(3),.base--button:nth-of-type(4),svg, iframe, #icon-id0996535026110407, #teconsent,#ads{
    display: none !important;
}

#root,.flex,.column,.column>p,.column>p>label,.base--ul{
    margin-top: 0 !important;
}

.dropzone {
    margin-left: 0 !important;
    margin-right: 0 !important;
    padding: 0rem 0rem !important;
}

/* sizing */

.base--button:nth-of-type(1) {
    margin-top: 0.5em !important;
    flex: none;
}

.tab-panels {
    height: 80% !important;
}

/* 
.tab-panels--tab-content>div{
    height: 80% !important;
} */

._container_large {
    max-width: 100%;
}


/* colours */

p,.tab-panels--tab-content>div>.speaker-labels>div>dt {
    color: #FFF !important;
}

.tab-panels--tab-content>div>.speaker-labels>div>dd {
    color: #FFF !important;
}

html,#root,.tab-panels--tab-content,.tab-panels--tab-content>div>.speaker-labels,.alert,.alert--content-container,.alert--icon-container, .dropzone ._container ._container_large{
    background-color: #000 !important;
}

.base--inline-label::before {
    color: white !important;
}

}
