css:
@-moz-document url-prefix("https://live.bilibili.com/") {
/* 房间本体 */
/* 房间广告 */
.room-body > a {
   display: none;
}
/* 屏蔽看板娘 */
.live-haruna-ctnr {
    display: none;
}
.right-container {
    display: none;
}
.room-feed {
    display: none;
}
/* 屏蔽主播头像装饰 */
.anchor-title-image {
    display: none;
}
/* 系统广播 */
.announcement-container {
    display: none;
}
/* 房间内通知 */
.system-msg {
    display: none;
}
/* 页面底部 */
.link-footer-ctnr,
.live-footer {
    display: none;
}
/* 推荐视频绘画 */
.activity-pushing-out,
.recommend-section {
    display: none !important;
}
/* 房间背景：淡灰 */
body,
.room-bk {
    background-color: #F0F0F0;
}
.room-bg,
.room-bk .bk-img {
	display: none;
}
.bk-changers {
	display: none;
}
}
