#card-title.ytd-search-refinement-card-renderer,
#guide-section-title.ytd-guide-section-renderer,
#label-text.yt-dropdown-menu,
#label.ytd-pinned-comment-badge-renderer,
#title.ytd-rich-grid-renderer,
#title.ytd-shelf-renderer,
#video-title-link #video-title.ytd-rich-grid-video-renderer,
#video-title.ytd-playlist-panel-video-renderer,
#video-title.ytd-rich-grid-media,
.title.ytd-mini-channel-renderer,
h1,
h2,
html.lqRCAz ytd-app #video-title
 {
	color: #F0E890!important;
	text-shadow: 1px 1px #000!important;
