/*___ Burlywood ____________________________________________________________________*/

#text-container.ytd-channel-name a,
#text.ytd-channel-name,
.badge-style-type-collection.ytd-badge-supported-renderer,
.sbqs_c b,
h3,
h4,
ytd-playlist-panel-video-renderer[watch-color-update_] #byline.ytd-playlist-panel-video-renderer,
ytd-post-renderer[uses-compact-lockup_] #author-text.yt-simple-endpoint.ytd-post-renderer
 {
	color: #e0b880!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/

#icon-label.yt-dropdown-menu,
h5,
h6,
label
 {
	color: #F0A060!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

#date.ytd-video-primary-info-renderer,
#metadata-line.ytd-grid-video-renderer span:nth-child(2),
#metadata-line.ytd-video-meta-block span.ytd-video-meta-block:nth-child(2),
#published-time-text.ytd-post-renderer a,
#stats.ytd-playlist-sidebar-primary-info-renderer span:nth-child(2),
#timestamp.yt-live-chat-text-message-renderer,
time
 {
	color: #07beff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green ________________________________________________________________________*/

.badge-style-type-ypc.ytd-badge-supported-renderer
 {
	color: #07ff24!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red __________________________________________________________________________*/


selector
 {
	color: #ff0707!important;
	
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
	padding-left: 6px!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder { /* Chrome, Firefox, Opera, Safari 10.1+ */
  color: #f48f8f;
  opacity: 1; /* Firefox */
}

/*___ Misc _________________________________________________________________________*/

.token.punctuation
 {
    color: #fff!important;
}

.token.selector, .token.attr-name, .token.string, .token.char, .token.builtin, .token.inserted {
    color: #af0!important;
}

.token.atrule, .token.attr-value, .token.keyword
 {
    color: #00b3ff!important;
}

.token.property, .token.tag, .token.boolean, .token.number, .token.constant, .token.symbol, .token.deleted
 {
    color: #ff6cbe!important;
}

.ytp-bound-time-left,
.ytp-bound-time-right,
.ytp-time-display,
.ytp-time-duration,
.ytp-time-separator
 {
    color: #fffcfc!important;
	text-shadow: 1px 1px #000!important;
}






















}

