css:
@-moz-document domain(esoacademy.com) {
  body {
      background: #131313 !important;
      color: #e5e5e5 !important;
  }
  body .normal-page, body .blog, .blog-post, .ubermenu-main {
      background-color: #3b3939 !important;
  }
  .widget-1, .widget-2, .wcontainer, .block_tabs .tab, .ubermenu-skin-vanilla-bar .ubermenu-submenu.ubermenu-submenu-drop {
      background-color: #2c2c2c !important;
  }
  .ubermenu-item > span, .ubermenu-item > a {
      color: #e5e5e5 !important;
  }
  .ubermenu-main .ubermenu-item-level-0 > .ubermenu-target {
      color: #e5e5e5 !important;
  }
  .home-blog-content {
      background-color: #2c2c2c !important;
  }
  .wpDataTablesWrapper table.wpDataTable tr.odd td {
      background-color: #151515 !important;
      color: #e5e5e5 !important;
  }
  .wpDataTablesWrapper table.wpDataTable tr.even td {
      background-color: #575656 !important;
      color: #e5e5e5 !important;
  }
}
