css:
@-moz-document domain("reddit.com") {
.link .score {
    display: none;
}
.Post__score {
    display: none;
}
.res-voteEnhancements-highlightScores span.score {
    display: none;
}
.arrow.up {
    display: none;
}
.arrow.upmod {
    display: none;
}
.likes .score.likes {
    display: none;
}
.Post__score.m-upvoted {
    display: none;
}
.VoteArrow.m-upvoted {
    display: none;
}
.arrow.down {
    display: none;
}
.VoteArrow.m-downvoted {
    display: none;
}
.VoteArrow.m-unvoted {
    display: none;
}
.dislikes .score.dislikes {
    display: none;
}
.arrow.downmod {
    display: none;
}
body > .content .link .rank,
.rank-spacer {
    display: none;
}
.likes div.score.likes {
    display: none;
}
.Comment__metadata {
    display: none
}
}
