css:
@-moz-document domain("liveomg.com") {
body,
header {
	background-color: #111 !important;
	color: #bababa;
}

.promo {
	background-color: #111 !important;
}

.promo:hover {
	color: #bd2b2b !important;
}

.filtermenu {
	background-color: #2f2f2f;
	color: #bababa !important;
}

.host {
	color: #bababa !important;
}

.viewers {
	color: #bababa !important;
}

.username {
	color: #8ca7c3 !important;
}

a {
	text-decoration: none !important;
	color: #bd2b2b;
}

.sett {
	background-color: #bbb;
	color: #f00;
}

h1 {
	color: #bd2b2b;
}

.dropdown-charts {
	background-color: #1a1a1a !important;
}

.dropdown-charts li {
	border-top: 1px solid #000;
}

.dropdown-charts li>a {
	color: #bababa;
}
}
