css:
@-moz-document domain("imgsrc.ru") {
html {
	background-color: #111 !important;
}

td {
	background-color: #111 !important;
	color: #999;
}

A {
	color: #c14b4b;
}

TABLE {
	background-color: #111 !important;
	color: #999;
}

select {
	background-color: #111 !important;
	color: #999;
}

input[name="str"] {
	background-color: #111 !important;
	color: #999;
}

input[type="submit"] {
	color: #111;
}

TH {
	background-color: #0d0d0d;
}

H1,
H2,
H5,
.topmenu A {
	color: #999;
}

.tomato {
	color: #CC0000 !important;
}

textarea {
	color: #999;
	border: solid 1px #999;
	background-color: #111 !important;
}
}
