css:
@-moz-document url-prefix("https://studio.code.org/s/") {
/* spacing between lesson title and puzzle numbers */
[style="margin-top: 20px; margin-bottom: 30px; font-size: 14px; font-family: \"Gotham 4r\", sans-serif;"] {
	margin-top: 0 !important;
	margin-bottom: .5em !important;
}

/* padding inside lesson boxes */
[style="padding: 20px;"] {padding: 1em !important}

/* spacing between lesson boxes */
[style$="margin: 3px 3px 15px;"] {margin: 0 3px !important}
[style$="margin: 0px 0px 12px;"] {margin: 0 !important}
}
