css:
@-moz-document url-prefix("https://www.sedar.com") {
body {
    background-color: #d2d2d2 !important;
}
table {
    background: rgba(200, 200, 200, 0) !important;
    border-radius: 6px !important}
table > tbody > tr:nth-child(even), table > tbody > tr > td:nth-child(even) {
    background-color: rgba(200, 200, 200, 0) !important}
table > tbody > tr:nth-child(odd), table > tbody > tr > td:nth-child(odd) {
    background-color: rgba(200, 200, 200, 0) !important}

}
