css:
@-moz-document domain("source.android.com") {
body {
    background-color: #232323
}
body,
html {
    color: #dedede;
}
h1,
.devsite-landing-row h2,
.devsite-landing-row-large-headings .devsite-landing-row-item-description h3,
.devsite-products-alphabet-letter-heading {
    color: #efefef;
}
.devsite-nav-item-heading > .devsite-nav-title {
    color: #fff;
}
.devsite-nav-title {
    color: #e9e9e9;
}
.devsite-section-nav {
    background: #151515;
}
.devsite-top-logo-row-wrapper {
    background: #212121;
}
.devsite-top-logo-row .devsite-overflow-tabs-scroll-button,
.devsite-top-logo-row .devsite-overflow-tabs-scroll-button:focus,
.devsite-top-logo-row .devsite-overflow-tabs-scroll-button:hover,
.devsite-top-logo-row .devsite-overflow-tabs-scroll-button:active {
    background: #212121;
}
input.devsite-search-field {
    background: #212121;
    color: #fff;
}
.devsite-header-upper-tabs .devsite-doc-set-nav-active {
    color: #FFFFFF;
}
.devsite-header-upper-tabs .devsite-doc-set-nav-tab {
    color: #FFFFFF;
}
.devsite-search-active .devsite-search-field {
    color: #fff;
}
.devsite-header-upper-tabs .devsite-doc-set-nav-tab:hover,
.devsite-header-upper-tabs .devsite-doc-set-nav-tab:focus {
    color: rgba(255, 255, 255, 0.9);
}
code,
pre,
.pre-style {
    background: #000;
    color: #fff;
}
aside,
aside code,
aside var,
aside:link,
aside:visited,
.note,
.note code,
.note:link,
.note:visited,
.special,
.special code,
.special:link,
.special:visited {
    background: #3a535f;
    color: #c1d5ff;
}
tr,
table.google-visualization-table-table .google-visualization-table-tr-head,
table.google-visualization-table-table .google-visualization-table-tr-even,
table.google-visualization-table-table .google-visualization-table-tr-odd {
    background: #1e3744;
    border: 0;
    border-top: 1px solid #232323;
}
.caution,
.caution code,
.caution:link,
.caution:visited {
    background: #382b17;
}
.devsite-search-image {
    color: rgba(254, 254, 254, 0.81);
}
input.devsite-search-field::placeholder {
    color: #fff;
}
.devsite-page-nav {
    background: #181818;
}
td,
table.responsive tr:not(.alt) td td:first-child,
table.responsive td tr:not(.alt) td:first-child,
table.google-visualization-table-table .google-visualization-table-td {
    background: rgb(56, 56, 56);
}
td,
td code,
table.google-visualization-table-table .google-visualization-table-td {
    color: #c9c9c9;
}
}
