css:
@-moz-document domain("animemusicquiz.com") {
#gameChatPage > .col-xs-9 {
    background-image: url(https://i.imgur.com/HWcg6AM.png);
    background-image: -webkit-image-set(url(https://i.imgur.com/HWcg6AM.png) 1x, url(https://i.imgur.com/HWcg6AM.png) 2x);
}
#gameContainer {
    background-image: url(https://i.imgur.com/HWcg6AM.png);
    background-image: -webkit-image-set(url(https://i.imgur.com/HWcg6AM.png) 1x, url(https://i.imgur.com/HWcg6AM.png) 2x);
}
#startPage {
    background-image: url(https://i.imgur.com/HWcg6AM.png);
    background-image: -webkit-image-set(url(https://i.imgur.com/HWcg6AM.png) 1x, url(https://i.imgur.com/HWcg6AM.png) 2x);
}
#awMainView {
    background-image: url(https://i.imgur.com/HWcg6AM.png);
    background-image: -webkit-image-set(url(https://i.imgur.com/HWcg6AM.png) 1x, url(https://i.imgur.com/HWcg6AM.png) 2x);
}
#loadingScreen {
    background-image: url(https://i.imgur.com/HWcg6AM.png);
    background-image: -webkit-image-set(url(https://i.imgur.com/HWcg6AM.png) 1x, url(https://i.imgur.com/HWcg6AM.png) 2x);
}
.particleEmiter {
    -webkit-box-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #fff, 0 0 70px #fff, 0 0 80px #fff, 0 0 100px #fff, 0 0 150px #fff;
    box-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #fff, 0 0 70px #fff, 0 0 80px #fff, 0 0 100px #fff, 0 0 150px #fff;
    -webkit-text-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #fff, 0 0 70px #fff, 0 0 80px #fff, 0 0 100px #fff, 0 0 150px #fff;
    text-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #fff, 0 0 70px #fff, 0 0 80px #fff, 0 0 100px #fff, 0 0 150px #fff;
}
.particle {
    background-color: #fff;
    -webkit-box-shadow: 0 0 5px #fff, 0 0 10px #fff, 0 0 15px #fff;
    box-shadow: 0 0 5px #fff, 0 0 10px #fff, 0 0 15px #fff;
}
.particleShadow {
    -webkit-box-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #fff, 0 0 70px #fff, 0 0 80px #fff, 0 0 100px #fff, 0 0 150px #fff;
    box-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #fff, 0 0 70px #fff, 0 0 80px #fff, 0 0 100px #fff, 0 0 150px #fff;
}
#xpBackground {
    background-color: #fff;
}
#xpBackground > .animationContainer {
    -webkit-box-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #fff, 0 0 70px #fff, 0 0 80px #fff;
    box-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #fff, 0 0 70px #fff, 0 0 80px #fff;
}
.greenInnerGlow > .animationContainer {
    -webkit-box-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #fff, 0 0 70px #fff, 0 0 80px #fff;
    box-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #fff, 0 0 70px #fff, 0 0 80px #fff;
}
.bigGreenGlow > .animationContainer {
    -webkit-box-shadow: 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #fff, 0 0 50px #fff, 0 0 90px #fff, 0 0 100px #fff, 0 0 110px #fff, 0 0 160px #fff;
    box-shadow: 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #fff, 0 0 50px #fff, 0 0 90px #fff, 0 0 100px #fff, 0 0 110px #fff, 0 0 160px #fff;
}
.levelUpHighLightText.textGlowGreen {
    text-shadow: 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #fff, 0 0 50px #fff, 0 0 90px #fff, 0 0 100px #fff, 0 0 110px #fff, 0 0 160px #fff;
}
#qpVoteSkip.toggled {
    background-color: #4497ea;
}
.qpAvatarAnswerContainer.rightAnswer .qpAvatarAnswer {
    text-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #4497ea, 0 0 70px #4497ea, 0 0 80px #4497ea, 0 0 100px #4497ea, 0 0 150px #4497ea;
}
.knownResult.correct {
    background-color: #4497ea;
    -webkit-box-shadow: 0 0 1px 2px #fff, 0 0 2px 2px #fff, 0 0 7px 2px #4497ea, 0 0 10px 2px #4497ea, 0 0 15px 2px #4497ea, 0 0 20px 2px #4497ea, 0 0 40px 2px #4497ea;
    box-shadow: 0 0 1px 2px #fff, 0 0 2px 2px #fff, 0 0 7px 2px #4497ea, 0 0 10px 2px #4497ea, 0 0 15px 2px #4497ea, 0 0 20px 2px #4497ea, 0 0 40px 2px #4497ea;
}
#qpUpvoteContainer.selected {
    background-color: #4497ea;
}
}
