css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("en.wikipedia.org") {
#column-content{margin: 0 !important;}
#content{
  margin: 7em 8px !important; border: 1px solid #aaa !important;} 
#p-search{
  width: 25em !important;
  position:absolute !important; 
  top:1em !important; 
  left: 8px !important;
  font-size: 20px !important;}
#searchBody{width: auto !important; 
  text-align: left !important;}
#searchInput{width: 20em !important;}
#searchGoButton, 
#donate,
#p-search h5,
#p-logo,
#p-coll-create_a_book,
#p-navigation,
#p-interaction,
#p-tb,
#p-lang,
#anontip,
.editsection,
.siteNoticeBig
{display: none !important;}
#p-cactions
{border:5px solid #000; top:5.75em !important; left: 0px !important}
}
