css:
@-moz-document domain('c-plusplus.net') {
.hljs {
    color: #555;
    border: 1px solid lightgray;
    background: #fafafa;
}

.hljs-keyword {
    color: #2c2cbb;
}

.hljs-title, .hljs-params {
    color: #555;
}

.hljs-meta {
    color: #1d8d1d;
}

.hljs-number, .hljs-string {
    color:#555;
}
}
