css:
@-moz-document url("https://mangadex.org"), url-prefix("https://mangadex.org") {
body {
    background-color: #2F3136;
}
.chapter-container a {
    color: #A0A0A0;
}
.navbar, .nav-link:hover, .nav-tabs .nav-link.active, .nav-tabs .nav-item.show .nav-link {
    background-color: #202225;
    background-image: none;
    border: none;
}
.nav-item:hover, .nav-pills .nav-link.active {
    background-color: #25262A;
    background-image: none;
    border: none;
}
.nav-item:hover {
    background-color: #2A2C31;
    background-image: none;
    border: none;
}
.nav, .card-header {
    background-color: #2A2C31;
    color: #A0A0A0;
    background-image: none;
    border: none;
}
.navbar-brand img {
    filter: invert(50%);
}
.nav {
    border-radius: 5px 5px 0 0;
}
.chapter-container, .card, .list-group-item {
    background-color: #36393F;
    border: none;
}
.chapter-container {
    padding: 16px;
    padding-top: 8px;
    border-radius: 0 0 5px 5px;
}
.card {
    padding-left: 8px;
}
.card-header {
    margin-left: -8px;
}
.border-bottom, .nav-tabs {
    border: none;
}
.dropdown-toggle.nav-link .d-xl-inline.d-lg-none {
    color: #A0A0A0 !important;
}
}
