css:
@-moz-document domain("reactjs.org") {
body {
   background-color: black;
   color: #c8c8c8;
}
h1, h3, p, li, a, button > div {
   color: #c8c8c8 !important;
}
article p > a, article li > a, section p > a {
   color: rgba(97, 218, 251, 0.75) !important;
   background: none !important;
   border-bottom-style: dashed !important;
   border-bottom-width: 1px !important;
   border-bottom-color: rgba(97, 218, 251, 0.6) !important;
   transition: color 0.5s
}
article p > a:hover, article li > a:hover, section p > a:hover {
   color: rgba(97, 218, 251, 1) !important;
   border-bottom-style: dashed !important;
   border-bottom-width: 1px !important;
   border-bottom-color: rgba(97, 218, 251, 1) !important;
}
article code {
   font-family: monospace !important;
   font-size: 14px !important;
}
p > code, li > code {
   background-color: #445555 !important;
   color: white !important;
   border-style: solid;
   border-width: 1px;
   border-color: #366363;
   transition: background-color 0.5s
}
p > code:hover, li > code:hover {
   background-color: #366363 !important;
}
article ~ div {
   border-color: #333333 !important;
}
article ~ div div {
   background-color: black !important;
   border-color: #333333 !important;
}
h2 {
   border-top: 1px solid #282828 !important;
}
}

@-moz-document url("https://reactjs.org/") {
header div > a {
   background: none !important;
}
section div > a {
   background: none !important;
}
}
