/* ==UserStyle==
@name           Wikipedia Redux
@namespace      felipedario.com
@version        1.0.0
@description    Wikipedia Redux style
@author         Felipe Dario
==/UserStyle== */
@-moz-document domain("wikipedia.org") {
    .mw-editsection, #siteSub {
        display: none;
    }
    
    ::selection {
  background: #fff3d4;
        color:#111;
}
    
    a:hover{text-decoration:none;}
    body {
        background: #fff;
    }
    #mw-head,
    #mw-panel , #footer{
        opacity: .1;
        -moz-transition: all 500ms ease-in;

    }
    #mw-panel:hover,
    #mw-head:hover, #footer {
        opacity: .75;
    
-moz-transition: all 500ms ease-in;
    }
    #mw-page-base {
        background: inherit
    }
    #content {
        border: none
    }
    #bodyContent {
        color: #666;
        font-size: 18px;
        line-height: 1.8em
    }
   
    .mw-body-content  a:link {
        color: #666;
        text-decoration: none;
        border-bottom: 6px solid #f5f5f5;
            -moz-transition: all 250ms ease-in;
    }
   
    .mw-body-content p:hover a,
    .mw-body-content ul:hover a,
   .mw-body-content  table:hover a {
      -moz-transition: all 250ms ease-in;
        color: blue;
        
    }

     .mw-body-content a:visited {
        border: none;
        text-decoration: line-through !important;
        color: #666;
        border-bottom: 6px solid #f5f5f5;
               -moz-transition: all 250ms ease-in;
    }
    
    
    
    .mw-body-content p:hover a:visited,
    .mw-body-content ul:hover a:visited,
   .mw-body-content  table:hover a:visited {
      -moz-transition: all 250ms ease-in;
        color: #ccc;
        
    }
    
    
    .mw-body-content a:hover {
        -moz-transition: all 250s ease-in;
        border-bottom: 6px solid blue
    }
    
    
        .mw-body-content a:hover:visited {
        -moz-transition: all 250s ease-in;
        border-bottom: 6px solid #ccc;
    }
    
    
    
    .hatnote,  .ambox{opacity:.5;}
    
    
    .mw-body h1{
   border:none;
      font-weight:100;
        font-size: 1.8em;
        font-family: sans-serif;
        color: #555;
       
      
    
    }
    .mw-body-content h2 {
        border-bottom: 2px solid #ddd;
        font-weight: bold;
        font-size: 1em;
        font-family: sans-serif;
        color: #888;
        text-transform: uppercase;
        letter-spacing: .25em;
        margin: 2em 0 1em;
        padding: 0 0 .5em;
        font-family: sans-serif;
    }
    .mw-body-content h3 {
        border-bottom: 2px solid #ddd;
        font-weight: normal;
        font-size: 1em;
        font-family: sans-serif;
        color: #888;
        text-transform: uppercase;
        letter-spacing: .25em;
        margin: 2em 0 1em;
        padding: 0 0 .5em;
        font-family: sans-serif;
    }
    .mw-body-content h4 {
        border-bottom: 2px solid #ddd;
        font-weight: normal;
        font-size: .8em;
        font-family: sans-serif;
        color: #999;
        text-transform: uppercase;
        letter-spacing: .5em;
        margin: 2em 0 1em;
        padding: 0 0 .25em;
        font-family: sans-serif;
    }
    .mw-body-content h5 {
        font-weight: normal;
        border-bottom: none;
        font-size: .8em;
        font-family: sans-serif;
        color: #999;
        text-transform: uppercase;
        letter-spacing: .25em;
        margin: 2em 0 1em;
        font-family: sans-serif;
    }
    .mw-body-content h6 {
        font-weight: normal;
        border-bottom: none;
        font-size: 1;
        font-family: sans-serif;
        color: #999;
        margin: 2em 0 1em;
        font-family: sans-serif;
    }
    #bodyContent p,
    #bodyContent ul {
        max-width: 600px;
    }
}
