@-moz-document domain("jmperezperez.com") {
body {
    background:#1a1a1a;
    color: #999
}

blockquote{
    background:#1a1a1a;
    border-left: 10px solid #1a1a1a;
}

.bmc-button{line-height:36px!important;height:37px!important;text-decoration:none!important;display:inline-flex!important;color:#FFF!important;background-color:#1a1a1a!important;border-radius:3px!important;border:1px solid transparent!important;padding:0 9px!important;font-size:12px!important;letter-spacing:-.08px!important;margin:0 auto!important;-webkit-box-sizing:border-box!important;box-sizing:border-box!important;-o-transition:.3s all linear!important;-webkit-transition:.3s all linear!important;-moz-transition:.3s all linear!important;-ms-transition:.3s all linear!important;transition:.3s all linear!important}

