css:
@-moz-document domain("diki.pl") {
body.dikibody {
    background-color: #000 !important;
}

div.dikibody div.diki-results-container {
    border: 1px solid #000 !important;
    background-color: black !important;
}

div.dikibody div.diki-results-container div.diki-results-left-column {
    border-right: 1px solid #444 !important;
}

div.dikibody div.diki-results-container div.diki-results-right-column {
    border-left: 1px solid #444 !important;
}

div.dikibody .foreignToNativeMeanings div.exampleSentence, div.dikibody .nativeToForeignEntrySlices div.exampleSentence {
    color: #ffffff !important;
}

div.dikibody ol.foreignToNativeMeanings span.hw, div.dikibody ol.nativeToForeignEntrySlices span.hw {
    color: #fff !important;
}

div.dikibody div.hws span.hw {
    color: #fff !important;
}

div.dikibody .foreignToNativeMeanings li.hl, div.dikibody .nativeToForeignEntrySlices li.hl {
    background-color: #222 !important;
}

body.dikibody, div.dikibody {
    color: white !important;
}

form.dikiSearchForm input[type="search"] {
    color: #ccc !important;
    background: #333 !important;
}


form.dikiSearchForm .autocompleteResults .ui-autocomplete {
    top: 0 !important;
    border: 1px dotted #c30407 !important;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    background: black !important;
    box-shadow: 0 1px 1px transparent !important;
}

p, ul, ol, td {
    color: #ccc !important;
}

form.dikiSearchForm .autocompleteResults li {
    background: black !important;
}

.qTipPopupModal.qtip .qtip-content {
    background: black;
}

.qTipPopupModal.qtip.qtipOverEntireViewport {
    background-color: black;
}

div.dikibody div.hws span.phoneticTranscription {
    background-color: white;
}

div.dikibody a, div.dikibody a:not(.noHover):hover {
    color: #91bfde;
}

div.dikibody a:visited {
    color: #91bfde;
}

#thinHeaderMenuIcon {
    background-color: #333;
}

#thinHeaderMenu {
    background-color: #444;
}

#thinHeaderMenu .menuItem.menuProfileItem {
    background-color: #333;
}

#thinHeaderMenu .menuItem * {
    color: #fff;
}
}
