/* ==UserStyle==
@name        Intel Article Wide
@namespace url(https://software.intel.com);
@version     1.0.0
@license     unlicense
==/UserStyle== */

@-moz-document domain(software.intel.com) {
      #menu-navigation.fixed-header {
          position: absolute;
      }

      body.has-inpage-navigation.node-type-article #page-wrapper #main .node-article .field-type-text-with-summary .field-item {
          max-width: 100%;
      }

      .node-type-article #page-wrapper #main .node-article div.field-type-text-with-summary p,
      .node-type-article #page-wrapper #main .node-article div.field-type-text-with-summary h2,
      .node-type-article #page-wrapper #main .node-article div.field-type-text-with-summary h3,
      .node-type-article #page-wrapper #main .node-article div.field-type-text-with-summary ul,
      .node-type-article #page-wrapper #main .node-article div.field-type-text-with-summary table
      {
          max-width: 100%;
      }

      .node-type-article #page-wrapper #main .node-article div.field-type-text-with-summary div {
          max-width: 100%;
      }

}
