css:
@-moz-document domain(twitch.tv) {

.tw-svg__asset.tw-svg__asset--share {
    position: relative;
    top: 2px;
}
.tw-svg__asset.tw-svg__asset--more {
    position: relative;
    top: -2px;
    left: -1px;
}
.tw-button {
    display: inline-block !important;
}
.tw-button__icon {
    display: inline-block !important;
    padding: 0.4rem !important;
    width: 32px;
}
.tw-interactive.tw-button.tw-button--hollow:not(.tw-button--dropmenu) .tw-button__text {
    position: relative;
    left: -6px;
}
.tw-button-icon {
    line-height: 0.6rem !important;
}
.tw-svg {
    position: relative;
    top: 1px;
    left: 1px;
}
.tw-button-icon--overlay .tw-svg {
    top: -3px;
    left: 0px;
}
button[data-test-selector="qa-video-options-button"] .tw-svg {
    top: -2px;
    left: 0px;
}
.tw-button[data-a-target="video-chat-submit-button"] {
    display: inline-flex !important;
}
.tw-button[data-a-target="video-chat-submit-button"] .tw-svg {
    top: 0px;
    left: 0px;
}
.player-button {
    height: 3.5em !important;
}
.player-play-overlay .player-button-play {
    height: 8em !important;
    margin-top: -4em !important;
}
.tw-button-icon::before {
    height: 1px;
}
.tw-button__icon--right {
    position: relative;
    top: 2px;
}
.tw-button--hollow .tw-button__text {
    top: -4px;
}

}
