css:
@-moz-document url-prefix("http://putlocker.us.com/") {
:root{
  --background-color:#111;
  --accent-color:#444;
  --text-color:#fff;
}

body{
  background-image:none!important;
  background-color:var(--background-color)!important;
}

.header{
  background-color:var(--background-color)!important;
}

.top-menu li a{
  color:var(--text-color)!important;
}

.vtabmenu ul li span.active, .pagination li{
  background-color:var(--accent-color)!important;
  color:var(--text-color)!important;
}
.vtabmenu ul li span{
  background-color:transparent!important;
  color:var(--text-color)!important;
}

.singleVideo .mli-info{
  color:var(--text-color)!important;
  background-color:rgba(0, 0, 0, 0.5)!important;
}

.pagination li span, .pagination li a, .filterAreah, .movies-letter a.btn-letter{
  color:var(--text-color)!important;
  background-color:var(--accent-color)!important;
}

.movies-letter a.btn-letter.active, .pagination li.active span, .pagination li.active aactive{
  background-color:#777!important;
}

.search-content .form-control.search-input{
  background-color:var(--accent-color)!important;
  color:var(--text-color)
}

s.cinema-dashboard ul.nav-tabs li.active a{
  background-color:transparent!important;
  color:var(--text-color)!important;
}

s.cinema-dashboard .tab-content ul.episodes>li>a{
  color:var(--text-color)!important;
}

s.hr-divider .hr-divider-heading{
 background-color:var(--background-color)!important;
 color:var(--text-color)!important;
}

.qtip:not(.ie9haxors) div.qtip-content, .qtip:not(.ie9haxors) div.qtip-titlebar, .jtip-top, .jt-imdb{
  background-color:var(--accent-color)!important;
  color:var(--text-color)!important;
}
.f-title, .f-desc, .jt-info, .qtip-content .block{
  color:var(--text-color)!important;
}

.topdescription, .chartdescriptionLeft ul li, .chartdescriptionRight ul li, .filterAreac{
  background-color:var(--background-color)!important;
  color:var(--text-color)!important;
}

.notice_area.clear, .facebookComarea.smallContainer, .slidershareslideareamainhomeshare.clear, .onp-sl-secrets .onp-sl-outer-wrap, .bottomdescription{
  display:none!important;
}
}
