@-moz-document url-prefix("https://www.ytlandia.com/") {
/*Theme made by Pixel World - Animecflife.blogspot.com

/*GNU General Public License v3.0*/

/*1.0*/

   body {
           background: #000;
           color: #dedee0;
        }

   p {     color: #dedee0;
        }
           .post-title
        {
           color: #fff  
        }
           .is-sidebar-style-1 .sidebar-inner    
        {
           background-color: #000000;
        }
           .navbar-wrap
        {
            background-color: #363537;
            color: #fff;
        }
           .ajax-filter-dropdown-list 
        {
    
            background-color: #000;
        }
              
       /*logo*/
      .header-style-4 .logo-wrap {
    display: table;
    height: 80px;
    background-color: black;
 }
        
           .widget-title.block-title {

           border-bottom: 2px solid rgba(245, 235, 235, 0.2);
        }
             body {
    display: block;
    background: #000;
    color: #7D7D81;
}

.ajax-filter-link:hover, .ajax-filter-link:hover, .ajax-filter-link.is-active {
    color: #f5f3f3;
}

               /*buscador en sidebar*/   
            body:not(.is-sidebar-style-2) .sidebar-wrap .widget_search .ruby-search input[type='text'] {
            background-color: #191919;
            }

            .ruby-search input[type="text"]:hover, .ruby-search input[type="text"]:focus, .ruby-search input[type="text"]:active {
             color: #fff;
             }

            input[type="text"], input[type="tel"], input[type="password"], input[type="email"], input[type="url"], input[type="search"] {
             color: #b1aaaa;
             }

          /*Noticias recientes en hover*/  
              .widget_recent_entries li a:hover, .recentcomments a:hover {
                color: #fff!important;
                opacity: .8;
              }

           /*Botones para compartir*/  
             .share-bar-el.icon-facebook i, .share-bar-el-big.icon-facebook { /*FB*/
               background-color: #04226b;
             }
             .share-bar-el.icon-twitter i, .share-bar-el-big.icon-twitter {   /*TW*/
               background-color: #0692bd;
             }

            /*Boton de notificaciones*/
              #onesignal-bell-container.onesignal-reset .onesignal-bell-launcher.onesignal-bell-launcher-theme-default .onesignal-bell-                      launcher-button svg .background {
                  fill: #8e0002;
              }
#onesignal-bell-container.onesignal-reset .onesignal-bell-launcher.onesignal-bell-launcher-theme-default .onesignal-bell-launcher-button svg .background {
    fill: #8a0002;
}

            /*header oscuro*/
.header-ad-outer {
    background-color: #201d1d;
}

            /*color de numeros en la paginacion*/          
            .page-numbers {
               color: black;
            }
}
