css:
@-moz-document domain("zimuzu.io"), domain("zimuzu.tv") {
body {
	background: url(https://i.loli.net/2018/08/21/5b7b539d96544.jpg);
	background-size: cover;
	background-attachment: fixed;
}

.box.score-box,
.box.clearfix.res-view-top {
	background: #efededc7;
}

.box,
.box.clearfix.res-imgs-box {
	background: #eee6e6eb;
}

.clearfix.res-bbs-box {
	background: #f9f8f89c;
}

.middle-box,.top-box{
	background: #efeded00;
}
DIV.top-box:nth-child(5) > DIV:nth-child(5){display:none}
}
