css:
@-moz-document domain("techblog.willshouse.com") {
body {
  background: #101010;
    color: #e5e5e5;
}

#wrapper {

    background: #101010;
    margin-top: 20px;
    padding: 0 20px;
    color: #e5e5e5;

}

h1, h2, h3, p, #site-title a {
    color: #e5e5e5;
}

#content, #content input, #content textarea {

    color: #e5e5e5;
    font-size: 16px;
    line-height: 24px;

}

#your-useragent{
    color:#101010;
}

#content textarea{
    background:#101010;
    color: #e5e5e5;
}

#content .entry-title, h3#comments-title, .comment-author cite, h3#reply-title{
    color: #e5e5e5; 
}

#content tr td{
    color: gray;
}
}

@-moz-document domain("techblog.willshouse.com") {
div.horz-ad > .inside, .widget-container, .adsbygoogle{
     visibility: hidden;
}
}
