/* ==UserStyle==
@name           Full width git lab review
@namespace      github.com/openstyles/stylus
@version        0.1.0
@description    A new userstyle
@author         Me
==/UserStyle== */
@-moz-document domain("git.sbis.ru") {
    .diff-collapsed {
        display: none;
    }

    .js-toggle-diff-comments {
        display: none !important;
    }

    .js-view-file {
        max-width: 35px;
        overflow: hidden;
    }

    .file-title-name {
        font-size: 11px;
        word-wrap: normal;
    }

    .file-header-content {
        word-wrap: normal;
        white-space: normal !important;
    }

    .file-title-flex-parent .file-header-content {
        word-wrap: normal;
        white-space: normal !important;
    }

    .diff-line-num {
        max-width: 20px;
    }

    .container-fluid.container-limited.limit-container-width {
        max-width: 100%;
        margin: 0;
        padding: 15px;
    }

    #diff-notes-app {
        width: 100%;
    }
}
