css:
@-moz-document domain("aftermarket.pl") {
*, b {
    background-color: #000000!important;
    color: #d4c5c5!important;
}
input, TABLE.thetable TD.headercolumn, TABLE.thetable TD.headercolumn A , A {
      color: #eae9e9!important;
}
a img {
    background: #e4b9b9!important;
    color: #ffffff!important;
}
TABLE.thetable {
    margin: 10px 0;
    border: 2px solid white!important;
}
.inline-menu-dropdown {
    position: relative;
    display: inline-block !important;
    border: 1px solid #ffffff;
}
#topmenu A.current,#topmenu A.current2, current, current2 {
    background-color: #000000 !important;

    border: 1px solid transparent!important;
  
    border-bottom: 1px solid white!important;
    
}
#topmenu A.current2{
border-bottom: 1px solid #E0E0E0!important;
}
ul.css-tabs a.current {
    border-top: 4px solid white;
    color: white;
    cursor: default;
}
.inputg{
      padding: 7px 0px 7px 64px!important;
}
}
