css:
@-moz-document domain("boards.4chan.org") {
.board,
.navLinks {
    margin: 0 70px;
}

#index-options select {
    margin-left: 5px;
}

.catalog-post {
    margin: 0 10px !important;
}

.catalog-thread {
    height: 250px !important;
}

.boardTitle {
    padding-top: 20px;
}

.postMessage {
    line-height: 1.35em;
    font-size: 13px;
}

blockquote.postMessage {
    margin:15px 30px;
}

div.postContainer.replyContainer {
    margin-bottom:18px;
}

#blotter,
#togglePostFormLink,
.aboveMidAd,
#bannerCnt,
a.hide-reply-button {
    display: none;
}
}
