css:
@-moz-document domain("nytimes.com") {
body {
    background: #222;
    color: #ccc;
}

a {
    background: #222 !important;
    color: #ccc !important;
    border-color: #ccc !important;
}

a:hover {
    background: #222 !important;
    color: #fff !important;
    border-color: #ccc !important;
    text-decoration: underline !important;
}

h1,
h2,
h3,
h4,
h5,
h6,
ul,
li,
span,
div,
article,
section,
header,
menu,
p,
nav,
::after {
    background: #222 !important;
    color: #ccc !important;
    border-color: #ccc !important;
}

svg,
path {
    fill: #ccc !important;
}
}
