css:
@-moz-document domain("angular.io") {
body, mat-toolbar-row {
    background: #222;
}

h1, h2, h3,h4,h5,h6,
header, nav, mat-sidenav, ul, li, a, button, table, th, tr, td,
footer, aio-shell, mat-sidenav-container,
p, div, section, article, span {
    background: none !important;
    color: #ccc !important;
    border-color: #ccc;
}

table, .docs-card {
    border: 1px solid #ccc;
}
code a {
    color: #222 !important;
}
}
