css:
@-moz-document url-prefix("https://www.gscq.me"), domain("gscq.me") {
【背景图片设置】,
          body {
          	background: url(https://i.loli.net/2018/08/21/5b7b539d96544.jpg)no-repeat;
          	background-size: cover;
          	background-attachment: fixed;
          }

          .card.card-threadlist,
          .card.card-site-info,
          .card.text-middle {
          	background: #ffffffde;
          	color: #226e0ffa;
          }

          .small.nav_tag_list,
          .breadcrumb.d-none.d-md-flex,
          .btn.btn-primary.btn-block.mb-3 .btn.btn-primary.btn-block.mb-3 {
          	background: #fefefe94;
          }

          .card.card-forum-info,
          .card.card-mod-info {
          	background: #fbfbfb94;
          }
       【首页热门帖子隐藏】,
          .card.text-middle {
          	display: none;
          }

        
          .card:last-child {
          	background: #f6f0f003;
          }
}
