css:
@-moz-document url-prefix("https://tools.ietf.org/html/rfc") {
@media print {
    body {
        margin: 0;
    }

    .content {
        width: 81ex;
        margin: 0 auto;
    }

    .docinfo {
        display: block;
    }

    .docinfo + br {
        display: none;
    }

    .docinfo.top + br {
        display: block;
    }

    .docinfo.top + br + .docinfo {
        display: none;
    }

    a:link,
    a:visited {
        color: blue;
    }
}
}
