css:
@-moz-document domain("libcom.org") {
#page, body, .block, #status, div.fb-like-box, ul.pager, ul.tabs.primary, ul.tabs.primary li, ul.tabs.primary li a, #footer-inner, #footer-outer, table, thead, tr, th, td, .comment .comment-prefix {
    background: rgb(48, 48, 48) !important;
}

p, span, #status a, b, em, label, div, ul {
    color: rgb(180, 180, 180) !important;
}

tr {
    border: 0px !important
}

h1, h2, h3 a, h3 {
    color: rgb(191, 191, 191) !important;
}

a, a h3 {
    color: rgb(157, 1, 1) !important;
}

#secondary-wrapper, #secondary-wrapper ul li a {
    background: rgb(133, 0, 0) !important;
    color: rgb(216, 216, 216) !important;
}

#prefix-wrapper, #logo a, #primary-links a, input {
    background: rgb(133, 0, 0) !important;
    color: rgb(216, 216, 216) !important;
}

#node-sidebar > div, #node-page, .page-frontpage #front-top-column-two .views-row, .book-navigation .menu, #comments, .comment, .book-navigation .page-links {
    border-top: 1px solid #5b0000;
}

blockquote {
    border-left: 5px solid rgb(180, 180, 180);
}

.page-frontpage #front-top-column-three .views-field-name, #footer .link-stack h2, .page-frontpage #front-top-column-two, .node.not-page, thead, .comment ul.links li, .book-navigation .page-links {
    border-bottom: 1px solid #5b0000;
}

.forum, #main-share {
    border-right: 1px solid #5b0000 !important;
}

#header .breadcrumb {
    margin: 0;
    padding: 0;
    color: #1f1e23;
    font-weight: bold;
    border-bottom: 1px dotted #890000;
    font-size: 11px;
}
}
