css:
@-moz-document domain("www.bilibili.com") {
/*调按控制栏，并设置外边框颜色*/
.mode-fullscreen .bilibili-player-video-control{
    background-color:#333 !important;
    border-color:#666 !important;
}

/*覆盖白色图标背景色*/
.mode-fullscreen .bilibili-player-video-btn:hover{
    background-color:rgba(255,255,255,0.1) !important;
}

/*调暗进度条*/
.mode-fullscreen .bpui-slider-tracker{
    background-color:rgba(255,255,255,0.4) !important;
}

/*针对设置宽屏按钮的颜色*/
.mode-fullscreen .bilibili-player-video-btn-widescreen,
.mode-fullscreen .bilibili-player-video-btn-widescreen:hover{
    background-color:rgba(255,255,255,0) !important;
}

/*音量选项*/
.mode-fullscreen .bilibili-player-video-volumebar-wrp{
    background-color:#333 !important;
    border-color:#666 !important;
}

/*清晰度选项*/
.mode-fullscreen .bpui-selectmenu-list{
    background-color:#333;
    border-color:#666 !important;
}
.mode-fullscreen .bpui-selectmenu-list-row{
    color:#aaa !important;
}
.mode-fullscreen .bpui-selectmenu-list-row:hover{
    background-color:#666 !important;
}
.mode-fullscreen .bpui-selectmenu-list-row[data-selected="selected"]{
    background-color:#666 !important;
}

/*弹幕设置选项*/
.mode-fullscreen .bilibili-player-danmaku-setting-lite{
    background-color:#333 !important;
}
.mode-fullscreen .bilibili-player-block-filter-label,
.mode-fullscreen .bpui-checkbox-text,
.mode-fullscreen .bilibili-player-danmaku-setting-lite-title{
    color:#aaa !important;
}
}
