css:
@-moz-document domain("universal-sci.com") {
    #siteWrapper
    {
        background-color: #212631 !important;
        color: rgba(232, 232, 232, .9) !important;
        font-size: 25px !important;
    }

    .hide-blog-sidebar.collection-type-blog #page #content 
    {
        max-width: 1020px !important;
    }

    h2, .summary-title a {
        color: #fff !important;
    }

    .comment-count
    {
        color: rgba(217, 217, 217, .86) !important;
    }

    .squarespace-comments .new-comment-area 
    {
        background-color: #000 !important;
    }

    .squarespace-comments .new-comment-area .input form.comment-form textarea
    {
        background-color: #202020 !important;
        color: #e8e8e8 !important;
    }

    .entry-tags, .entry-tags a
    {
        color: #e8e8e8 !important;
    }

    .social-icons-color-black.social-icons-style-regular .sqs-svg-icon--list .sqs-svg-icon--wrapper .sqs-use--icon
    {
        fill: #ffffff !important;
    }

    .social-icons-color-black.social-icons-style-regular .sqs-svg-icon--list:hover .sqs-use--icon
    {
        fill: #cfcfcf !important;
    }
}
