css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://joop.bnnvara.nl") {

    body[class^="post"],[class^="home"] {
    
    background-image: none;
    
    background-color: #000;

    color: #AAA;

    }
  
    main {

    background-color: #000;

    color: #AAA;

    }


    header[class^="site"] {

    background-color: #000;

    color: #AAA;

    }

    p {

    background-color: #000;

    color: #AAA;

    }

    div {

    background-color: #000;

    color: #AAA;

    }

    section[class^="widget"] {

    background-color: #000;

    color: #AAA;

    }

    aside[class^="widget"] {

    background-color: #000;

    color: #AAA;

    }

    a[class^="logo"] {

    background-color: #fafafa;

    color: #AAA;

    }
}
