css:
@-moz-document url("https://developer.acaprojects.com/") {
    
    .sidebar, body {
        background-color: #121214;
        color: #a6bfd8;
    }

    .sidebar-toggle {
        background-color: hsla(205.6, 28.1%, 52.5%, 0.8);
    }

    .markdown-section p.tip, .markdown-section tr:nth-child(2n) {
        background-color: #566e80;
    }

    .markdown-section h1, .markdown-section h2, .markdown-section h3, .markdown-section h4, .markdown-section strong {
        color: #3e71a5;
    }

    .anchor span {
        color: #336fab;
    }

    .markdown-section em {
        color: #49b3bc;
    }

    .markdown-section a, .sidebar ul li a {

        color: #69a3d5;
    }

    .sidebar ul li.active > a, .token.string {
        color: #42b983;
    }

    .markdown-section pre > code, .markdown-section code, .markdown-section pre {
        background-color: #322d2d;
        color: #cbcbcb;
    }

    .markdown-section p.tip, .markdown-section tr:nth-child(2n) {
        background-color: #19242c;
    }

    .token.boolean, .token.number, .token.keyword, .markdown-section code {
        color: #8ada71;
    }
    
}
