body {
    background-color: #141d26;
    color: #FF691F !important;
}




/* i really want this to be global */
.navbar-inner {
    background-color: #243447;
}




/* i really want this to be global */
.container-fluid {
    color: #FF691F !important;
}




/* i really want this to be global */
.block {
    background-color: #243447;
}




/* i really want this to be global */
.block .header h2 {
