css:
@-moz-document domain("eksiduyuru.com")
{
::-moz-selection {
    background: #b4d5e3;
}

body 
{background: #212736;}

.whitebox, 
.notify, 
.soncev
{background: #8a8f97;}

.notify a, 
.scii a, 
.soncev a
{color: #fff;}

.notify a:hover, 
.scii a:hover, 
.soncev a:hover, 
.url:hover, 
.poster a:hover, 
.paginate a:hover
{color: #c0ddff;}

.entrybody a, 
.paginate a
{color: #85d1f5;}

.entry, 
.entry0, 
.entry1, 
.entry2, 
.entry3, 
#newannouncement, 
#freetext
{
    color: #000;
    background: #71787e;}

.bottomleft, 
.cnt
{color: #000;}

.bottomright a, 
.url, 
.poster a
{color: #fff;}

.bottomright
{background: #71787e;}

#benimkilerSelector, 
div.entryhead img.finger
{background: #d6d6d6;}
}
