css:
@-moz-document domain("www.dn.se"), url-prefix("http://videostream.dn.ua/"), url-prefix("https://videostream.dn.ua/")
{
.headersport, #main-wrapper {
    background: #222!important;
    border: 0.1px solid #000!important;
}

.sports_tab, .videolink, .bg_mid {
    background: #444!important;
    border: 0.1px solid #000!important;
}

a, span {
    color: white!important;
    text-shadow: none;
}

i {
    color: #ccc!important;
}

.videolink:hover {
    background: grey!important;
}

.headersport:hover {
    border: 0.1px solid #3a3a3a!important;
}
}
