css:
@-moz-document domain("lichess.org") {
div[data-id="1+0"], div[data-id="2+1"], div[data-id="3+0"], div[data-id="3+2"], div[data-id="5+0"], div[data-id="5+3"], div[data-id="10+0"], div.custom {
    visibility: hidden;    
}

div[data-id="15+15"] {
    position: absolute;
    top: 0;
    left: 0;
    width: 100% !important;
    height: 100% !important;
}
}
