css:
/*
    YouTube Focus v1
    GitHub Link: https://github.com/qheaden/userstyles-youtube-focus
*/

@-moz-document domain('www.youtube.com'), domain('m.youtube.com') {
    /* Desktop Site Rules */
    ytd-thumbnail,ytd-comments,ytd-menu-renderer {
        display: none;
    }

    #secondary {
        display: none;
    }

    /* Mobile Site Rules */
    ytm-comment-section-renderer {
        display: none;
    }

    [data-content-type="related"] {
        display: none;
    }

    .large-media-item-thumbnail-container, .video-main-content-like-dislike {
        display: none;
    }
}
