css:
@-moz-document domain("wol.jw.org") { /* remove this line and ending '}' if not working with chrome*/
body {
  background-color: #1d252a;
  color: #b5b8ba;
}

h1, h2, h3, .st, .ss {
  color: #fbb23c;
}

a {
  color: #77a0c8;
}

#siteBanner {
  background-color: #344450;
}

#menuHome a {
  background-color: #1d252a;
}

.menu a {
  color: #7c7d7e;
}

#toolbarFrame {
  background-color: #1d252a;
}

.searchForm .searchButton {
  background-color: #1d252a;
  color: #77a0c8;
  border-color: #77a0c8;
}

#publicationNavigation {
  background-color: #1d252a;
}

#publicationNavigation li {
  color: #b5b8ba;
}

#publicationNavigation .navPosition {
  color: #b5b8ba;
}

#menuFrame {
  background-color: #222d34;
}

@media only screen and (max-width: 1024px), screen and (max-height: 700px) {
	.dir-ltr #menuFrame {
    	background-color: #1d252a;
	}
}

#regionMain {
  background-color: #222d34;
}

/* home page/landing page */
#home {
  background-color: #222d34;;
}

#dailyText {
  background-color: #344450;
}

.dir-ltr #dailyText {
  border-left-color: #1d252a;
}

/* bible page */
.materialNav {
  background-color: #344450;
}

ul.directory li a {
  color: #b5b8ba;
}

ul.directory li.row:not(:last-child) {
  border-color: #344450;
}

ul.directory li.viewPage.selected a .title {
  color: #7c7d7e;
}

ul.bibleAdditionalLinks li {
  border-color: #4e7fbc;
}

ul.bibleAdditionalLinks li:not(.disabled):hover {
  background-color: #4e7fbc;
}

.bible nav.shaded .group {
  color: #7c7d7e;
}

/* daily text page */
#article {
  background-color: #344450;
}

#todayNav a {
  border-color: #344450;
}

.card {
  background-color: #222d34;
}

.linkCard .card {
  background-color: #222d34;
}

.dir-ltr .itemCaption .linkCard a .card .icon {
  background-color: #222d34;
}

.shadedHeader {
  color: #1d252a;
}

/* publications */
.articlePositioner {
  background-color: #344450;
}

#secondaryNav {
  background-color: #222d34;
}

#secondaryNavContent {
  background-color: #222d34;
}

.resultNavTitle {
  margin-top: 0;
  padding-top: 2%;
}

.resultNavTitle li {
  color: #b5b8ba;
}

ul.navigationTabs {
  background-color: #1d252a;
}

.tabItem.active {
  background-color: #222d34;
  border-bottom-color: #222d34;
  color: #b5b8ba;
}

#similarMaterial .simDoc:not(:first-child) {
  border-top-color: #222d34;
}

.documentNavigation li {
  border-bottom-color: #222d34;
}

.contextTtl, .featureTtl {
  color: #fbb23c;
}

figcaption {
  color: #77a0c8;
}

/* publication boxSupplement */
.boxSupplement {
  background-color: #1d252a;
}

.boxTtl {
  color: #fbb23c;
  background-color: #1d252a;
}

.boxSupplement.rule .boxContent {
  background-color: #1d252a;
  border-color: #1d252a;
}

/* publication questions */
.qu {
  color: #fbb23c;
}

/* tool tip box */
.tooltip {
  background-color: #344450;
  color: #b5b8ba;
}

.tooltipContent p, h1 {
  color: #b5b8ba!important;
}

.tooltip .tooltipClose {
  background-color: #222d34;
}

.tooltip .tooltipContent {
  background-color: #344450;
}

.tooltip .tooltipContent .tooltipText {
  background-color: #344450;
}

/* tooltip with search */
.article.search .sb,.article.search p,.tooltipContent .sb,.tooltipContent p,.unstyled .sb,.unstyled p {
  color: #b5b8ba!important;
}

.article .sf, .article .sq {
  color: #77a0c8;
}

#contentFooterNavigation {
  background-color: #1d252a;
}

#contentFooterLegal {
  background-color: #192024;
}

}
