@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.engadget.com") {
  #outerslice, #statscol, .skyscraper, .google_units, .bottomleader, #grid, .topleader, #macworld-banner { display: none !important;}
  #subcontent {width: 164px !important;}
  #wrapper{ padding-right: 173px !important;}
  #bp1, #bp2, #bp3, #bp4, #bp5, #bp6, #bp7, #bp8, #bp9{display:none !important;}
  iframe{ display: none !important; }
  #underlogo, #tiplink, #lang { display: none !important; }
  #content {padding-top: 15px !important; }
  #innercontent div { border-bottom: #ccc thin solid;}
  #innercontent div div { border-bottom: none;}
  .post img { max-height: 150px;}
}
